import React, { Component, useState, Fragment } from "react";
import "./method.css";

class method extends Component {
  state = {
    meths: [""],
    methsPic: [],
    fileURL: [],
  };

  handleText = (i) => (e) => {
    let meths = [...this.state.meths];
    meths[i] = e.target.value;
    this.setState({
      meths,
    });
  };

  handleFile = (i) => (e) => {
    let methsPic = [...this.state.methsPic];
    methsPic[i] = e.target.files;
    this.setState({
      methsPic,
    });
  };

  handleDelete = (i) => (e) => {
    e.preventDefault();
    let meths = [
      ...this.state.meths.slice(0, i),
      ...this.state.meths.slice(i + 1),
    ];
    let methsPic = [
      ...this.state.methsPic.slice(0, i),
      ...this.state.methsPic.slice(i + 1),
    ];

    this.setState({
      meths: meths,
      methsPic: methsPic,
    });
  };

  addMethod = (e) => {
    e.preventDefault();
    let meths = this.state.meths.concat([""]);
    this.setState({
      meths,
    });
  };

  getFilename(i) {
    if (this.state.methsPic[i]) {
      console.log(this.state.methsPic[i][0].name);
      return this.state.methsPic[i][0].name;
    } else {
      return i;
    }
  }

  getListFileName() {
    let fileList = [];
    this.state.meths.map((meth, i) => {
      //console.log(i);
      if (this.state.methsPic[i]) {
        fileList = [...fileList, this.state.methsPic[i][0].name];
      } else {
        fileList = [...fileList, i.toString()];
      }
    });

    return fileList;
  }

  render() {
    //console.log(this.state.meths);

    return (
      <Fragment>
        {this.state.meths.map((meth, index) => (
          <span key={index}>
            <textarea
              type="text"
              rows="7"
              onChange={this.handleText(index)}
              value={meth}
            />

            <input
              key={index}
              type="file"
              id={index}
              name={index}
              accept="image/*"
              className="inputFile"
              onChange={this.handleFile(index)}
            ></input>
            <label key={index} htmlFor={index}>
              <div key={index} className="dropZone">
                Choose File
              </div>
              <img src={this.state.fileURL[index]} id="pic" name="file" />
            </label>

            <button id="xbut" onClick={this.handleDelete(index)}>
              X
            </button>
          </span>
        ))}
        <button onClick={this.addMethod}>Add more method</button>
      </Fragment>
    );
  }
}

export default method;

