import React, { Component, useState, Fragment } from "react";
import "./method.css";

class method extends Component {
  state = {
    meths: [""],
    methsPic: [],
  };

  handleText = (i) => (e) => {
    let meths = [...this.state.meths];
    meths[i] = e.target.value;
    this.setState({
      meths,
    });
  };

  handleFile = (i) => (e) => {
    let methsPic = [...this.state.methsPic];
    methsPic[i] = e.target.files;
    this.setState({
      methsPic,
    });
  };

  handleDelete = (i) => (e) => {
    e.preventDefault();
    let meths = [
      ...this.state.meths.slice(0, i),
      ...this.state.meths.slice(i + 1),
    ];
    let methsPic = [
      ...this.state.methsPic.slice(0, i),
      ...this.state.methsPic.slice(i + 1),
    ];

    this.setState({
      meths: meths,
      methsPic: methsPic,
    });
  };

  addMethod = (e) => {
    e.preventDefault();
    let meths = this.state.meths.concat([""]);
    this.setState({
      meths,
    });
  };

  getFilename(i) {
    if (this.state.methsPic[i]) {
      console.log(this.state.methsPic[i][0].name);
      return this.state.methsPic[i][0].name;
    } else {
      return i;
    }
  }

  render() {
    //console.log(this.state.methsPic);
    //const fileInputKey = input.value ? input.value.name : +new Date();
    return (
      <Fragment>
        {this.state.meths.map((meth, index) => (
          <span key={index}>
            <textarea
              type="text"
              rows="7"
              onChange={this.handleText(index)}
              value={meth}
            />
            <input
              key={() => this.getFilename(index)}
              type="file"
              id={index}
              name="img"
              accept="image/*"
              onChange={this.handleFile(index)}
            ></input>

            <button id="xbut" onClick={this.handleDelete(index)}>
              X
            </button>
          </span>
        ))}
        <button onClick={this.addMethod}>Add more method</button>
      </Fragment>
    );
  }
}

export default method;

